import React, { useState, useEffect, useRef } from "react";
import { Box, TextField, Button, Typography } from "@mui/material";
import { styled } from '@emotion/styled';

const ChatContainer = styled(Box)({
  border: "1px solid #ccc",
  margin: "50px",
  padding: "20px",
  height: "400px",
  overflowY: "auto",
  display: "flex",
  flexDirection: "column",
  gap: "15px"
});

const UserMessage = styled(Typography)({
  alignSelf: "flex-end",
  color: "blue"
});

const BotMessage = styled(Typography)({
  alignSelf: "flex-start",
  color: "red"
});

const Form = styled('form')({
  display: "flex",
  justifyContent: "center",
  margin: "20px 0",
  gap: "15px"
});
const DcotorAI = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const endOfMessages = useRef(null);

  const scrollToBottom = () => {
    endOfMessages.current.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(scrollToBottom, [messages]);

  const fetchResponse = async (message) => {
    const response = await fetch("/apis/doctorai/", {
      method: "POST",
      body: JSON.stringify({
        message: message,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await response.json();
    return data;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessages([...messages, { text: input, sender: "user" }]);
    const data = await fetchResponse(input);
    setMessages((prevMessages) => [
      ...prevMessages,
      { text: data.message, sender: "bot" },
    ]);
    setInput("");
  };

  return (
    <Box textAlign="center">
      <Box className={classes.chatContainer}>
        {messages.map((message, index) => (
          <Typography key={index} className={classes[message.sender]}>
            {message.text}
          </Typography>
        ))}
        <div ref={endOfMessages} />
      </Box>
      <form onSubmit={handleSubmit} className={classes.form}>
        <TextField
          variant="outlined"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type a message"
          fullWidth
        />
        <Button variant="contained" color="primary" type="submit">Send</Button>
      </form>
    </Box>
  );
};

export default DcotorAI;
